#include<vector>
using namespace std;
int search(vector<int> lst){
	int n = lst.size();
	int max = 0;
	int max_index = -1;
	for(int i = 0; i < n; i++){
		if(lst[i] > max){
			max = lst[i];
			max_index = i;
		}
		if(lst[i] > max && lst[i] > lst[max_index]){
			max = lst[i];
			max_index = i;
		}
	}
	return max_index;
}
