using namespace std;
vector<int> count_up_to(int n){
    vector<int> primes;
    int i = 2;
    while(i<=n){
        if(is_prime(i)){
            primes.push_back(i);
        }
        i++;
    }
    return primes;
}
bool is_prime(int n){
    if(n==1) return false;
    if(n==2) return true;
    if(n%2==0) return false;
    for(int i=3;i<=sqrt(n);i+=2){
        if(n%i==0) return false;
    }
    return true;
}
