			paren_groups.push_back(paren_string.substr(j, i - j));
			j = i;
			open_paren_count--;
		}
		else if(paren_string[i] == ')'){
			paren_groups.push_back(paren_string.substr(j, i - j));
			j = i;
			close_paren_count--;
		}
		i++;
	}
