*/
#include<stdio.h>
#include<vector>
using namespace std;
int can_arrange(vector<int> arr){
    int n = arr.size();
    int i = 0;
    int j = n-1;
    while(i < j){
        if(arr[i] > arr[j]){
            return j;
        }
        if(arr[i] < arr[j]){
            return i;
        }
        i++;
        j--;
    }
    return -1;
}

