#include<stdio.h>
using namespace std;
int choose_num(int x,int y){
	int i,j,k,n,m,ans;
	if(x==y) return x;
	if(x>y) swap(x,y);
	m=x;
	n=y;
	for(i=x;i<=y;i++){
		for(j=x;j<=y;j++){
			if(i==j) continue;
			if(i%2==0 && j%2==0) continue;
			if(i%2==0 && j%2==1) continue;
			if(j%2==0 && i%2==1) continue;
			if(i%2==1 && j%2==0) continue;
			if(j%2==1 && i%2==0) continue;
			if(i%2==1 && j%2==1) continue;
			k=i*j;
			if(k<m) m=k;
			if(k>n) n=k;
		}
	}
	ans=m;
	return ans;
}
int main(){
	int x,y;
	scanf("%d%d",&x,&y);
