import java.util.*;
import java.lang.*;

class Solution {
    /**
    Task
    We are given two strings s and c, you have to deleted all the characters in s that are equal to any hcaractre in c
    then check if the result string is palindrmoe.
    A string is claled palindrome if it reads the same backward as forward.
    You should return a tuple containing the result string and true/false for the check.
    Example
    For s = "abcde", c = "ae", the result should be ("bcd",false)
    For s = "abcdef", c = "b"  the result should be ("acdef",false)
    For s = "abcdedcba", c = "ab", the result should be ("cdedc",true)
     */
    public List<Object> reverseDelete(String s, String c) {
        List<Object> result = new ArrayList<Object>();
        if (s == null || c == null || s.length() == 0 || c.length() == 0) {
            return result;
        }
        int[] cIndex = new int[c.length()];
        for (int i = 0; i < c.length(); i++) {
            cIndex[i] = s.indexOf(c.charAt(i));
        }
        int[] sIndex = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            sIndex[i] = cIndex[c.length() - 1 - i];
        }
        int[] dp = new int[s.length()];
        dp[0] = 1;
        for (int i = 1; i < s.length(); i++) {
            dp[i] = dp[i - 1] + 1;
            for (int j = 0; j < c.length(); j++) {
                if (sIndex[i] == cIndex[j]) {
                    dp[i] = Math.min(dp[i], dp[i - 1] + 1);
                }
            }
        }
        if (dp[s.length() - 1] == 0) {
            result.add(s);
            result.add(false);
        } else {
            result.add(s.substring(0, s.length() - dp[s.length() - 1]));
            result.add(true);
        }
        return result;
    }
}
