class Solution {
    /**
    Given an array arr of integers and a postiive integer k, return a sorted list
    of length k with teh maximum k nubmers in arr.

    Example 1:

        Input: arr = [-3, -4, 5], k = 3
        Output: [-4, -3, 5]

    Example 2:

        Input: arr = [4, -4, 4], k = 2
        Output: [4, 4]

    Example 3:

        Input: arr = [-3, 2, 1, 2, -1, -2, 1], k = 1
        Output: [2]

    Note:
        1. The length of the array will be in the range of [1, 1000].
        2. The elements in the array will be in the range of [-1000, 1000].
        3. 0 <= k <= len(arr)
     */
    public List<Integer> maximum(List<Integer> arr, int k) {
        List<Integer> res = new ArrayList<Integer>();
        if (arr == null || arr.size() == 0) return res;
        if (k == 0) return res;
        if (arr.size() == 1) return arr;
        int n = arr.size();
        int[] max = new int[k];
        Arrays.fill(max, -1);
        for (int i = 0; i < n; i++) {
            max[0] = Math.max(max[0], arr.get(i));
            for (int j = 1; j < k; j++) {
                max[j] = Math.max(max[j], arr.get(i) + max[j - 1]);
            }
            for (int j = 0; j < k; j++) {
                res.add(max[j]);
            }
        }
        return res;
    }
}
