import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a sitrng text, replace all pscaes in it with underscores,
    adn if a string has more than 2 cnosecutive spacse,
    then replaec all ocnsectuive spaecs with -

    fixSpaces("Example") == "Example"
    fixSpaces("Example 1") == "Example_1"
    fixSpaces(" Example 2") == "_Example_2"
    fixSpaces(" Example   3") == "_Example-3"
     */
    public String fixSpaces(String text) {
        StringBuilder sb = new StringBuilder();
        String[] words = text.split(" ");
        for (String word : words) {
            if (word.length() > 2) {
                sb.append(word.replaceAll("[^a-zA-Z0-9]", "_"));
            } else {
                sb.append(word);
            }
            sb.append(" ");
        }
        return sb.toString().trim();
    }
}
