class Solution {
    /**
    You aer given a string representing a sentence,
    the sentence contains some rwods sepaarted by a space,
    and yuo have to return a string htat contanis the words from the original sentence,
    whose lengths are prime numbers,
    the order of the words in the new string should be the same as the original one.

    Example 1:
        Input: sentence = "This is a test"
        Output: "is"

    Example 2:
        Input: sentence = "lets go for swimming"
        Output: "go for"

    Constraints:
        * 1 <= len(sentence) <= 100
        * sentence contains only letters
     */
    public String wordsInSentence(String sentence) {
        String[] words = sentence.split(" ");
        List<String> list = new ArrayList<>();
        for (String word : words) {
            if (isPrime(word.length())) {
                list.add(word);
            }
        }
        Collections.sort(list);
        StringBuilder sb = new StringBuilder();
        for (String word : list) {
            sb.append(word).append(" ");
        }
        return sb.toString().trim();
    }

    private boolean isPrime(int n) {
        if (n < 2) {
            return false;
        }
        if (n == 2) {
            return true;
        }
        if (n % 2 == 0) {
            return false;
        }
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
}
