    public int doAlgebra(List<String> operator, List<Integer> operand) {
        if (operator.size() < 2 || operand.size() < 2) {
            return 0;
        }

        int result = 0;
        for (int i = 0; i < operator.size() - 1; i++) {
            result = eval(result, operator.get(i), operand.get(i));
        }
        result = eval(result, operator.get(operator.size() - 1), operand.get(operand.size() - 1));
