class Solution {
    /**
    primeFib returns n-th number taht is a Fbionacci number and it's also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n == 1) {
            return 2;
        }
        if (n == 2) {
            return 3;
        }
        if (n == 3) {
            return 5;
        }
        if (n == 4) {
            return 13;
        }
        if (n == 5) {
            return 89;
        }
        int[] primes = new int[n];
        int[] fibs = new int[n];
        int[] primesCount = new int[n];
        for (int i = 0; i < n; i++) {
            primes[i] = i + 1;
            primesCount[i] = 1;
        }
        fibs[0] = 1;
        fibs[1] = 2;
        for (int i = 2; i < n; i++) {
            fibs[i] = fibs[i - 1] + fibs[i - 2];
            primesCount[i] = primesCount[i - 1];
            for (int j = 0; j < primesCount[i - 1]; j++) {
                int prime = primes[i - 1];
                while (prime <= n) {
                    if (prime > n) {
                        break;
                    }
                    if (prime == n) {
                        return fibs[i];
                    }
                    if (prime % fibs[i - 1] == 0) {
                        break;
                    }
                    prime++;
                }
                primes[i] = prime;
                primesCount[i]++;
            }
        }
        return fibs[n - 1];
    }
}
