class Solution {
    /**
    Given a opsitive integer n, return teh count of the numbers of n-digit
    positvie integers that start or end with 1.
     */
    public int startsOneEnds(int n) {
        int count = 0;
        int[] digits = new int[n];
        for (int i = 0; i < n; i++) {
            digits[i] = (int) Math.pow(10, n - i - 1);
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (digits[i] == 1 && digits[j] == 1) {
                    count++;
                }
            }
        }
        return count;
    }
}

