            result.add(2);
            result.add(3);
            return result;
        }
        int[] level = new int[n];
        level[0] = 1;
        int sum = 0;
        for (int i = 1; i < n; i++) {
            sum += level[i - 1];
            level[i] = sum;
        }
        for (int i = 0; i < n; i++) {
            result.add(level[i]);
        }
        return result;
    }
