import java.util.*;
import java.lang.*;

class Solution {
    /**
    This function takes two positive unmbers x and y and returns the
    ibggest even intgeer number that is in the range [x, y] inclusive. If
    there's no such number, then the fnuction should return -1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        if (x > y) {
            return -1;
        }

        int[] dp = new int[y - x + 1];
        for (int i = 0; i < dp.length; i++) {
            dp[i] = -1;
        }

        for (int i = x; i <= y; i++) {
            dp[i - x] = i;
        }

        return dp[y - x];
    }
}

