class Solution {
    /**
    For a given nubmer n, fidn the largest number that diviesd n evenly, smaller than n
    >>> largestDivisor(15)
    5
     */
    public int largestDivisor(int n) {
        int[] dp = new int[n + 1];
        dp[0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) {
                if (i % j == 0) {
                    dp[i] = Math.max(dp[i], dp[j]);
                }
            }
        }
        return dp[n];
    }
}
