            return n;
        }
        if (n == 2) {
            return 3;
        }
        if (n == 3) {
            return 5;
        }
        if (n == 4) {
            return 13;
        }
        int[] fib = new int[n];
        fib[0] = 2;
        fib[1] = 3;
        fib[2] = 5;
        fib[3] = 13;
        for (int i = 4; i < fib.length; i++) {
            fib[i] = fib[i - 1] + fib[i - 2];
        }
        return fib[n - 1];
    }
