class Solution {
    /**
    Input to this function is a bowed stringed instrument represented multiple groups for nest digression separate by space. 
     For each of the group, output the deep level of nesting of digression. 
     E.g. (() ()) has maximum two grade of nest while (( ()) ) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> result = new ArrayList<Integer>();
        if (paren_string == null || paren_string.length() == 0) {
            return result;
        }
        Stack<Integer> stack = new Stack<Integer>();
        for (int i = 0; i < paren_string.length(); i++) {
            if (paren_string.charAt(i) == '(') {
                stack.push(0);
            } else if (paren_string.charAt(i) == ')') {
                int level = stack.pop();
                result.add(level);
            }
        }
        return result;
    }
