     */
    public List<Integer> removeDuplicates(List<Integer> numbers) {
        if (numbers == null || numbers.isEmpty()) {
            return new ArrayList<>();
        }

        List<Integer> result = new ArrayList<>();
        Set<Integer> set = new HashSet<>();
        for (Integer num : numbers) {
            if (set.add(num)) {
                result.add(num);
            }
        }
        return result;
    }
}
