class Solution {
    /**
    Given a positive integer n, you have to make a pile of n degree of stone. 
     The inaugural degree experience n stones. 
     The issue of stones in the adjacent degree is: 
         - the next leftover number if n is odd. 
         - the future even number if n is even. 
     Return the act of stones in each level in a list, where component at index 
     i typify the issue of stone in the level (i+1).

     example:
    >>> makeAPile(3)
    [3, 5, 7]
     */
    public List<Integer> makeAPile(int n) {
        List<Integer> result = new ArrayList<Integer>();
        if (n == 0) {
            result.add(0);
            return result;
        }
        int[] levels = new int[n];
        int[] left = new int[n];
        int[] right = new int[n];
        int i = 0;
        levels[0] = 1;
        left[0] = 0;
        right[0] = 0;
        while (i < n) {
            int leftNum = left[i];
            int rightNum = right[i];
            int nextNum = 0;
            if (i < n - 1) {
                nextNum = left[i + 1];
            }
            if (i > 0) {
                nextNum = right[i - 1];
            }
            if (i == 0) {
                nextNum = 1;
            }
            if (leftNum == 0 && rightNum == 0) {
                levels[i] = 1;
                result.add(1);
                i++;
            } else if (leftNum == 0) {
                levels[i] = 2;
                result.add(2);
                i++;
            } else if (rightNum == 0) {
                levels[i] = 3;
                result.add(3);
                i++;
            } else {
                levels[i] = 4;
                result.add(4);
                i++;
            }
            left[i] = nextNum;
            right[i] = nextNum;
        }
        return result;
    }
}

