    public boolean isEqualToSumEven(int n) {
        if (n < 0) {
            return false;
        }
        if (n == 0) {
            return true;
        }
        if (n % 2 == 0) {
            return false;
        }
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += i;
        }
        return sum % 2 == 0;
    }
