    >>> makeAPile(3)
    [3, 5, 7]
     */
    public List<Integer> makeAPile(int n) {
        List<Integer> list = new ArrayList<Integer>();
        if (n == 0) {
            return list;
        }
        int[] level = new int[n];
        int[] stones = new int[n];
        int[] num = new int[n];
        int i = 0;
        level[0] = 0;
        stones[0] = 0;
        num[0] = 0;
        for (i = 1; i < n; i++) {
