import java.util.*;
import java.lang.*;

class Solution {
    /**
    This funct iontakest wo pos itive num bers x and y and retu rns the
    big g est even integer nu mber that is in the r ange [x, y]i nclusive. If
   there'sn o such number, then the func ti on should return -1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        if (x == 0 || y == 0) return 0;
        if (x == 1 || y == 1) return 1;
        if (x > y) {
            int tmp = x;
            x = y;
            y = tmp;
        }
        int[] dp = new int[x + 1];
        dp[0] = 1;
        for (int i = 1; i <= y; i++) {
            for (int j = 1; j <= x; j++) {
                if (i - j >= 0) {
                    dp[j] += dp[j - 1];
                }
                if (i - j < 0) {
                    dp[j] += dp[j - 1];
                }
            }
        }
        return dp[x];
    }
}
