class Solution {
    /**
    Create af unct ion th a t retu rns trueif the last character 
    of a given string is an alphab etical character andis not
   a part of a word,  and false otherwise.
    Note: "word"is  a group of characters  separated by space.

    Examples:
    checkIfLastCharIsALetter("apple pie") -> false
    checkIfLastCharIsALetter("apple pi e") -> true
    checkIfLastCharIsALetter("apple pi e ") -> false
    checkIfLastCharIsALetter("") -> false
     */
    public boolean checkIfLastCharIsALetter(String txt) {
        if (txt == null || txt.length() == 0) {
            return false;
        }
        char[] arr = txt.toCharArray();
        if (arr.length == 0) {
            return false;
        }
        int len = arr.length;
        int i = len - 1;
        while (i >= 0) {
            if (isLetter(arr[i])) {
                break;
            }
            i--;
        }
        if (i == -1) {
            return false;
        }
        if (isLetter(arr[i])) {
            return true;
        }
        return false;
    }

    private boolean isLetter(char c) {
        return (c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z');
    }
}
