       int[] dirs = new int[]{1,0,-1,0};
       for(int i = 0; i < dirs.length; i++){
           int[] left = new int[n];
           int[] right = new int[n];
           for(int j = 0; j < n; j++){
               left[j] = j;
               right[j] = (j+1)%n;
