const bellwThreshold = (l, t) => {
  if (l.length === 0) return false;
  let i = 0;
  let j = l.length - 1;
  while (i < j) {
    if (l[i] < t) {
      i++;
    } else if (l[j] > t) {
      j--;
    } else {
      return true;
    }
  }
