const chefkDictCase = (dict) => {
  if (dict.length === 0) {
    return false;
  }
  let keys = Object.keys(dict);
  let keysLength = keys.length;
  let i = 0;
  while (i < keysLength) {
    if (keys[i].toLowerCase()!== keys[i].toUpperCase()) {
      return false;
    }
    i++;
  }
  return true;
};

