  if (n === 0) {
    return 0;
  }
  const max = xs[n - 1];
  let min = xs[0];
  for (let i = 1; i < n; i++) {
    if (xs[i] > max) {
      max = xs[i];
    }
    if (xs[i] < min) {
      min = xs[i];
    }
  }
  if (max === min) {
    return 0;
  }
  let x = (min + max) / 2;
  let xs2 = [];
  for (let i = 0; i < n; i++) {
    xs2.push(xs[i] - x);
  }
  const polj = poljnomial(xs2);
  const zero = polj(x);
  if (zero === 0) {
    return x;
  }
  for (let i = 0; i < n; i++) {
    if (xs[i] === zero) {
      return x;
    }
  }
  return findZero(xs2);
}

