/*Complete the function that takes two integers and returns 
  the product of their unit digits.
  Assume the input is always valid.
  Examples:
  mulTiplY(148, 412) should return 16.
  mulTiplY(19, 28) should return 72.
  mulTiplY(2020, 1851) should return 0.
  mulTiplY(14,-15) should return 20.
  */
const mulTiplY = (a, b) => {
  let a_ = a.toString().split("").map(x => parseInt(x));
  let b_ = b.toString().split("").map(x => parseInt(x));
  let product = a_[0] * b_[0];
  for (let i = 1; i < a_.length; i++) {
    product *= a_[i];
  }
  for (let i = 1; i < b_.length; i++) {
    product *= b_[i];
  }
  return product;
};

console.log(mulTiplY(148, 412));
console.log(mulTiplY(19, 28));
console.log(mulTiplY(2020, 1851));
console.log(mulTiplY(14, -15));
