const gottenOdderCollatz = (n) => {
  let collatz = [1];
  let result = [];
  while (collatz.length > 0) {
    let current = collatz.shift();
    if (current % 2 === 0) {
      result.push(current);
    } else {
      collatz.push(current / 2);
      collatz.push(current * 3 + 1);
    }
  }
  return result;
};

