  4. The date should be in the format: mm-dd-yyyy

  for example: 
  validDates('03-11-2000') => true

  validDates('15-01-2012') => false

  validDates('04-0-2040') => false

  validDates('06-04-2020') => true

  validDates('06/04/2020') => false
  */
const validDates = (date) => {
  const dateParts = date.split('-');
  const month = dateParts[0];
  const day = dateParts[1];
  const year = dateParts[2];

  if (month < 1 || month > 12) {
    return false;
  }

  if (day < 1 || day > 31) {
    return false;
  }

  if (day < 1 || day > 30) {
    return false;
  }

  if (day < 1 || day > 29) {
    return false;
  }

  if (year < 2000 || year > 2099) {
    return false;
  }

  return true;
};

module.exports = validDates;
