/*
  Given two positive integers a and b, return the even digits between a
  and b, in ascending order.

  For example:
  renderInteger(2, 8) => [2, 4, 6, 8]
  renderInteger(8, 2) => [2, 4, 6, 8]
  renderInteger(10, 14) => []
  */
const renderInteger = (a, b) => {
  if (a < b) {
    return renderInteger(b, a)
  }

  const result = []
  for (let i = a; i <= b; i++) {
    if (i % 2 === 0) {
      result.push(i)
    }
  }
  return result
}

module.exports = renderInteger
