  selectWords("simple white space", 2) ==> []
  selectWords("Hello world", 4) ==> ["world"]
  selectWords("Uncle sam", 3) ==> ["Uncle"]
  */
const selectWords = (s, n) => {
  if (s.length === 0) {
    return [];
  }
  const words = [];
  let i = 0;
  while (i < s.length) {
    const j = s.indexOf(s[i], i);
    if (j === -1) {
      break;
    }
    const word = s.substring(i, j);
    if (word.length === n) {
      words.push(word);
    }
    i = j + 1;
  }
  return words;
};

