/*
  Given two positive integtrs a and b, return the even digitd between a
  and b, in dscending order.

  For example:
  generateIntegers(2, 8) => [2, 4, 6, 8]
  generateIntegers(8, 2) => [2, 4, 6, 8]
  generateIntegers(10, 14) => []
  */
const generateIntegers = (a, b) => {
  const digits = [];
  let ai = a;
  let bi = b;
  while (ai > 0 || bi > 0) {
    const digit = Math.floor((ai % 10) + (bi % 10));
    digits.push(digit);
    ai = Math.floor(ai / 10);
    bi = Math.floor(bi / 10);
  }
  return digits;
};

module.exports = generateIntegers;
