const isPalindrome = (text) => {
  if (text.length === 0) {
    return true;
  }
  let first = text[0];
  let last = text[text.length - 1];
  let middle = text.substring(1, text.length - 1);
  return (first === last && middle.split('').reverse().join('') === middle);
};

