  console.assert(add(1, 0) === 1)
  console.assert(add(2, 3) === 5)
  console.assert(add(5, 7) === 12)
  console.assert(add(7, 5) === 12)

  for (let i = 0; i < 100; i++) {
    let x = getRandomIntInclusive()
    let y = getRandomIntInclusive()
    console.assert(x + y === add(x, y))
  }
}

