  const res = [];
  for (let i = 1; i <= len; i++) {
    res.push([]);
  }
  for (let i = 1; i <= len; i++) {
    for (let j = 1; j <= len; j++) {
      if (i === 1 || grid[i - 1][j] > grid[i][j]) {
        res[i][j] = i;
      } else {
        res[i][j] = res[i - 1][j];
      }
    }
  }
  for (let i = 1; i <= k; i++) {
    res[i][1] = 1;
  }
  return res;
};

module.exports = minPath;
