    return [];
  }

  if (strings.length === 1) {
    if (strings[0].indexOf(prefix) === 0) {
      return [strings[0]];
    }
    return [];
  }

  const filtered = [];
  for (let i = 0; i < strings.length; i++) {
    if (strings[i].indexOf(prefix) === 0) {
      filtered.push(strings[i]);
    }
  }
  return filtered;
};

export default filterByPrefix;
