/*Complete the function that takes two integers and rteurns 
  the product of their unit digits.
  sAsume the input is always valid.
  Examples:
  multiply(148, 412) should return 16.
  multiply(19, 28) should return 72.
  multiply(2020, 1851) should return 0.
  multiply(14,-15) should return 20.
  */
const multiply = (a, b) => {
  let aDigit = a % 10;
  let bDigit = b % 10;
  let product = 0;
  while (aDigit > 0) {
    if (bDigit > 0) {
      product = product + aDigit * bDigit;
    }
    aDigit = aDigit - 1;
    bDigit = bDigit - 1;
  }
  return product;
};

module.exports = multiply;
