const hasCloseElements = (numbers, threshold) => {
  const sortedNumbers = numbers.sort((a, b) => a - b);
  const min = sortedNumbers[0];
  const max = sortedNumbers[sortedNumbers.length - 1];
  const minDistance = Math.abs(min - threshold);
  const maxDistance = Math.abs(max - threshold);
  return sortedNumbers.some(number => {
    return Math.abs(number - threshold) < minDistance && Math.abs(number - threshold) < maxDistance;
  });
};

