  */
const eat = (number, need, remaining) => {
  if (number < 0 || number > 1000) return [];
  if (need < 0 || need > 1000) return [];
  if (remaining < 0 || remaining > 1000) return [];

  const result = [];
  const total = number + need;
  const left = remaining - need;

  if (total > left) {
    result.push(total);
    result.push(left);
  } else {
    result.push(total);
    result.push(0);
  }

  return result;
};

export default eat;
