  if (len === 0) return -1
  let low = 0
  let high = len - 1
  while (low <= high) {
    const mid = (low + high) >> 1
    if (lst[mid] < lst[mid + 1]) {
      low = mid + 1
    } else {
      high = mid - 1
    }
  }
  if (lst[low] === lst[low + 1]) {
    return low
  } else {
    return -1
  }
}

