const largestPrimeFactor = (n) => {
  if (n < 2) {
    return 0
  }
  if (n % 2 === 0) {
    return 2
  }
  let i = 3
  while (i <= Math.sqrt(n)) {
    if (n % i === 0) {
      return i
    }
    i += 2
  }
  return n
}

