  */
const median = (l) => {
  let n = l.length;
  let m = n / 2;
  let i = 0;
  let j = n - 1;
  let k = m;

  while (k > 0) {
    if (l[i] > l[j]) {
      i++;
      k--;
    } else if (l[i] < l[j]) {
      j--;
      k--;
    } else {
      break;
    }
  }

  if (k == 0) {
    return l[i];
  } else if (k == 1) {
    return l[(i + j) / 2];
  } else {
    return l[i] + (l[j] - l[i]) / 2;
  }
};

export default median;
