const isPalindrome = (text) => {
  if (text.length < 2) {
    return true;
  }
  let first = text.charAt(0);
  let last = text.charAt(text.length - 1);
  let middle = text.substring(1, text.length - 1);
  return first === last && isPalindrome(middle);
};

