const getOddCollatz = (n) => {
  let arr = [];
  let i = 1;
  while (i <= n) {
    if (i % 2 === 0) {
      arr.push(i);
    } else {
      arr.push(i * 3 + 1);
    }
    i = i * 2 + 1;
  }
  return arr.sort((a, b) => a - b);
};

