const median = (l) => {
  if (l.length === 0) {
    return NaN;
  }
  let mid = Math.floor(l.length / 2);
  let left = l.slice(0, mid);
  let right = l.slice(mid, l.length);
  return (left.length + right.length) / 2;
};

module.exports = median;
