  let digits = x.toString();
  let length = digits.length;
  let i = 0;
  while (i < length) {
    result += digits[i];
    i += 1;
    if (i < length) {
      result += '0';
    }
  }
  if (shift > length) {
    result = reverse(result);
  }
  return result;
};

/**
  * Reverse the digits of the string.
  *
  * @param {string} s
  * @return {string}
  */
const reverse = (s) => {
  let result = '';
  let length = s.length;
  for (let i = length - 1; i >= 0; i -= 1) {
    result += s[i];
  }
  return result;
};

/**
  * Convert a string to a number.
  *
  * @param {string} s
  * @return {number}
  */
const stringToNumber = (s) => {
  let result = 0;
  let length = s.length;
  for (let i = 0; i < length; i += 1) {
    result += parseInt(s[i], 10);
  }
  return result;
};

/**
  * Convert a number to a string.
  *
  * @param {number} n
  * @return {string}
  */
const numberToString = (n) => {
  let result = '';
  let digits = n.toString();
  let length = digits.length;
  for (let i = 0; i < length; i += 1) {
    result += digits[i];
  }
  return result;
};

/**
  * Convert a string to a boolean.
  *
  * @param {string} s
  * @return {boolean}
  */
const stringToBoolean = (s) => {
  let result = false;
  if (s === 'true') {
    result = true;
  }
  return result;
};

/**
  * Convert a boolean to a string.
  *
  * @param {boolean} b
  * @return {string}
  */
const booleanToString = (b) => {
  let result = '';
  if (b) {
    result = 'true';
  }
  return result;
};

/**
  * Convert a string to a date.
  *
  * @param {string} s
  * @return {Date}
  */
const stringToDate = (s) => {
  let result = new Date();
  let date = s.split(' ');
  let year = date[0];
  let month = date[1];
  let day = date[2];
  let hours = date[3];
  let minutes = date[4];
  let seconds = date[5];
  let milliseconds = date[6];
  let time = '';
  if (year) {
    result.setFullYear(year);
  }
  if (month) {
    result.setMonth(month - 1);
  }
  if (day) {
    result.setDate(day);
  }
  if (hours) {
    result.setHours(hours);
  }
  if (minutes) {
    result.setMinutes(minutes);
  }
  if (seconds) {
    result.setSeconds(seconds);
  }
  if (milliseconds) {
    result.setMilliseconds(milliseconds);
  }
  return result;
};

/**
  * Convert a date to a string.
  *
  * @param {Date} d
  * @return {string}
  */
const dateToString = (d) => {
  let result = '';
  let date = d.toISOString();
  let year = date.substring(0, 4);
  let month = date.substring(5, 7);
  let day = date.substring(8, 10);
  let hours = date.substring(11, 13);
  let minutes = date.substring(14, 16);
  let seconds = date.substring(17, 19);
  let milliseconds = date.substring(20, 23);
  let time = '';
  if (year) {
    result += year;
  }
