  */
const rightAngleTriangle = (a, b, c) => {
  const hypotenuse = Math.sqrt(Math.pow(a, 2) + Math.pow(b, 2));
  const angle = Math.acos(Math.abs(a / hypotenuse));
  const angle2 = Math.acos(Math.abs(b / hypotenuse));
  const angle3 = Math.acos(Math.abs(c / hypotenuse));
  return (angle2 > angle && angle2 > angle3) || (angle2 < angle && angle2 < angle3);
};

