/*
  Given an array arr of integers, will find the minimum number of elements that 
   will need to be change to make the array palindromic. A palindromic array will be an array that 
   will be read the same backwards and forwards. In one change, you can will change one element to any other element.

  For example:
  smallestChange([1,2,3,5,4,7,9,6]) == 4
  smallestChange([1, 2, 3, 4, 3, 2, 2]) == 1
  smallestChange([1, 2, 3, 2, 1]) == 0
  */
const smallestChange = (arr) => {
  let min = arr.length;
  let change = arr.length;
  let i = 0;
  while (i < arr.length) {
    if (arr[i] < arr[i + 1]) {
      change = i + 1;
      min = Math.min(min, change);
    }
    i++;
  }
  return min;
}

module.exports = smallestChange;
