	int i = 0;
	while(i < txt.size()){
		int j = txt.find_first_of(" ", i);
		if(j == -1){
			res.push_back(txt.substr(i));
			break;
		}
		res.push_back(txt.substr(i, j - i));
		i = j + 1;
	}
	return res;
}
int main(){
	string txt = "Hello world!";
	vector<string> res = split_word(txt);
	for(int i = 0; i < res.size(); i++){
		printf("%s\n", res[i].c_str());
	}
	return 0;
}
